// Funci√≥n para mostrar secciones
function mostrarSeccion(id) {
    document.getElementById('seccionGeo').style.display = 'none';
    document.getElementById('seccionCanvas').style.display = 'none';
    document.getElementById('seccionVideo').style.display = 'none';

    const seccion = document.getElementById(id);
    seccion.style.display = 'block';

    if (id === 'seccionVideo' && typeof iniciarCamara === 'function') {
        iniciarCamara();
    }
}